css:
.flex-container {
  display: flex;
  flex-wrap: nowrap;
}

.flex-container > div {
  width: 33.33% ;

}
.center{
    text-align:center;
    border:2px solid black;
}
.left-side{
    margin-top: 60px;
}
.middle-side{
    margin-top: 120px;
}

table, th, td {
  border: 1px solid black;
}
.square{
      border: 2px solid black;
      margin-top:5px;
      width: 200px;
      height: 200px;
}
.square p{
    margin-top:160px;
    text-align: center;
}
.rect p{
    margin-top:100px;
    text-align:center;
}
.left-box{
    width:60%;
}
.right-box{
    width:40%;
}
.rect{
    border: 2px solid black;
    margin-left:100px;
    width: 300px;
    height: 130px;
}

.left-plot{
    width: 33.33%;
}
.middle-plot{
    width: 33.33%;
}
.right-plot{
    width:33.33%;
}
.right-name{
    margin-top:30px;
}
 .page-break {
      page-break-after: always;
  }
